import React, { useState, useEffect } from 'react';
import Timer from './Timer';
import Scoreboard from './Scoreboard';
import questions from './questions';
import './QuizPage.css';

const QuizPage = () => {
    const [showInstructions, setShowInstructions] = useState(true);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [userAnswers, setUserAnswers] = useState({});
    const [quizCompleted, setQuizCompleted] = useState(false);
    const [score, setScore] = useState(0);
    const [timeLeft, setTimeLeft] = useState(30);
    
    useEffect(() => {
        if (!showInstructions && timeLeft > 0) {
            const timer = setInterval(() => setTimeLeft(timeLeft - 1), 1000);
            return () => clearInterval(timer);
        }
    }, [timeLeft, showInstructions]);

    const handleStartQuiz = () => {
        setShowInstructions(false);
    };

    const handleAnswerSelect = (answer) => {
        setUserAnswers({ ...userAnswers, [currentQuestionIndex]: answer });
        if (questions[currentQuestionIndex].answer === answer) {
            setScore(score + 1);
        }
        nextQuestion();
    };

    const nextQuestion = () => {
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            setTimeLeft(30);
        } else {
            setQuizCompleted(true);
        }
    };

    return (
        <div className="quiz-container">
            {showInstructions ? (
                <div className="instructions">
                    <h2>Instructions</h2>
                    <ul>
                        <li>For multiple-choice questions, select the one best answer (A, B, C, or D).</li>
                        <li>For integer-type questions, write your numerical answer clearly.</li>
                        <li>No calculators unless specified.</li>
                        <li>You have 30 minutes to complete this quiz.</li>
                    </ul>
                    <button onClick={handleStartQuiz}>Start Quiz</button>
                </div>
            ) : quizCompleted ? (
                <Scoreboard score={score} />
            ) : (
                <div className="question-section">
                    <h3>Question {currentQuestionIndex + 1}</h3>
                    <p>{questions[currentQuestionIndex].question}</p>
                    {questions[currentQuestionIndex].options ? (
                        <ul>
                            {questions[currentQuestionIndex].options.map((option, index) => (
                                <li key={index} onClick={() => handleAnswerSelect(option)}>{option}</li>
                            ))}
                        </ul>
                    ) : (
                        <input
                            type="number"
                            onChange={(e) => setUserAnswers({ ...userAnswers, [currentQuestionIndex]: e.target.value })}
                        />
                    )}
                    <Timer timeLeft={timeLeft} />
                </div>
            )}
        </div>
    );
};

export default QuizPage;
