import React, { useState, useEffect } from "react";
import Timer from "../components/Timer";
import Scoreboard from "../components/Scoreboard";
import questions from "../components/Question";
import "./QuizPage.css";
import { saveQuizAttempt } from "../utils/IndexedDB";

const QuizPage = () => {
    const [showInstructions, setShowInstructions] = useState(true);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [userAnswers, setUserAnswers] = useState({});
    const [quizCompleted, setQuizCompleted] = useState(false);
    const [score, setScore] = useState(0);
    const [timeLeft, setTimeLeft] = useState(30);
    const [selectedAnswer, setSelectedAnswer] = useState("");
    const [attempts, setAttempts] = useState([]);
    const [showScore, setShowScore] = useState(false);

    useEffect(() => {
        if (showInstructions || quizCompleted) return;

        const timer = setInterval(() => {
            setTimeLeft((prevTime) => {
                if (prevTime === 1) {
                    clearInterval(timer);
                    nextQuestion(); 
                    return 0;
                }
                return prevTime - 1;
            });
        }, 1000);

        return () => clearInterval(timer);
    }, [showInstructions, quizCompleted, currentQuestionIndex]);


    // Open IndexedDB on component mount
    useEffect(() => {
        const request = indexedDB.open("QuizDatabase", 1);

        request.onupgradeneeded = (event) => {
            let db = event.target.result;
            if (!db.objectStoreNames.contains("quizHistory")) {
                db.createObjectStore("quizHistory", { keyPath: "id", autoIncrement: true });
            }
        };

        request.onsuccess = (event) => {
            const db = event.target.result;
            fetchQuizHistory(db); // Load history from IndexedDB
        };

        request.onerror = (event) => {
            console.error("IndexedDB error:", event.target.error);
        };
    }, []);

    // Fetch quiz history from IndexedDB
    const fetchQuizHistory = (db) => {
        const transaction = db.transaction("quizHistory", "readonly");
        const store = transaction.objectStore("quizHistory");
        const request = store.getAll();

        request.onsuccess = (event) => {
            setAttempts(event.target.result);
        };

        request.onerror = (event) => {
            console.error("Failed to fetch quiz history:", event.target.error);
        };
    };

    // Save quiz history in IndexedDB
    const saveQuizHistory = (finalScore) => {
        const request = indexedDB.open("QuizDatabase", 1);

        request.onsuccess = (event) => {
            const db = event.target.result;
            const transaction = db.transaction("quizHistory", "readwrite");
            const store = transaction.objectStore("quizHistory");

            const attempt = {
                date: new Date().toLocaleString(),
                score: finalScore,
                answers: userAnswers,
            };

            store.add(attempt);
        };

        request.onerror = (event) => {
            console.error("Failed to save quiz history:", event.target.error);
        };
    };

    const handleStartQuiz = () => {
        setShowInstructions(false);
        setCurrentQuestionIndex(0);
        setTimeLeft(30);
    };

    const handleAnswerSelect = (answer) => {
        setSelectedAnswer(answer);
    };

    const handleSubmitAnswer = () => {
        if (!selectedAnswer) return;

        const currentQuestion = questions[currentQuestionIndex];

        if (currentQuestion.type === "multiple-choice") {
            setUserAnswers((prevAnswers) => ({
                ...prevAnswers,
                [currentQuestionIndex]: selectedAnswer
            }));

            if (selectedAnswer === currentQuestion.correct) {
                setScore((prevScore) => prevScore + 1);
            }
        } else if (currentQuestion.type === "integer") {
            if (parseInt(selectedAnswer) === currentQuestion.correct) {
                setScore((prevScore) => prevScore + 1);
            }
            setUserAnswers((prevAnswers) => ({
                ...prevAnswers,
                [currentQuestionIndex]: parseInt(selectedAnswer)
            }));
        }

        nextQuestion();
    };

    const nextQuestion = () => {
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
            setTimeLeft(30);
            setSelectedAnswer("");
        } else {
            handleFinishQuiz();
            saveQuizHistory(score);
        }
    };

    const calculateScore = () => {
        return score; // Returns the final score
    };

    const handleFinishQuiz = async () => {  // ✅ Add async keyword
        const finalScore = calculateScore();
        
        try {
            await saveQuizAttempt(finalScore);  // ✅ Use await properly
            setAttempts((prevAttempts) => [
                ...prevAttempts,
                { date: new Date().toLocaleString(), score: finalScore }
            ]);
            setQuizCompleted(true);
            setShowScore(true);
        } catch (error) {
            console.error("Failed to save quiz attempt:", error);
        }
    };
    

    return (
        <div className="quiz-container">
            {showInstructions ? (
                <div className="instructions">
                    <h2>Instructions</h2>
                    <ul>
                        <li>Select the best answer for each question.</li>
                        <li>For integer-type questions, enter a numerical answer.</li>
                        <li>You have 30 seconds per question.</li>
                    </ul>
                    <button onClick={handleStartQuiz}>Start Quiz</button>
                </div>
            ) : showScore ? (
                <Scoreboard attempts={attempts} /> // ✅ Passes correct prop
            ) : (
                <div className="question-section">
                    <h3>Question {currentQuestionIndex + 1}</h3>
                    <p>{questions[currentQuestionIndex].question}</p>

                    {questions[currentQuestionIndex].type === "multiple-choice" ? (
                        <ul className="options-list">
                            {questions[currentQuestionIndex].options.map((option, index) => (
                                <li key={index} className="option-item">
                                    <label>
                                        <input
                                            type="radio"
                                            name="answer"
                                            value={option}
                                            checked={selectedAnswer === option}
                                            onChange={() => handleAnswerSelect(option)}
                                        />
                                        {option}
                                    </label>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <input
                            type="number"
                            className="integer-input"
                            value={selectedAnswer}
                            onChange={(e) => setSelectedAnswer(e.target.value)}
                        />
                    )}

                    <Timer timeLeft={timeLeft} />
                    <button onClick={handleSubmitAnswer} disabled={!selectedAnswer} className="next-button">
                        Next
                    </button>
                </div>
            )}
        </div>
    );
};

export default QuizPage;
